package main

import (
	"fmt"
)

func findSubstring(s string, words []string) []int {
	ret := make([]int, 0)

	copyWords := make([]string, len(words))
	for i := 0; i < len(s); i++ {
		copyWords = copyWords[0:len(words)]
		copy(copyWords, words)
		if helpString(s[i:], copyWords) {
			ret = append(ret, i)
		}
	}

	return ret
}

func rmElFromSlice(sl []string, str string) ([]string, bool) {
	for i, val := range sl {
		if val == str {
			sl = append(sl[0:i], sl[i+1:]...)
			return sl, true
		}
	}
	return sl, false
}

func helpString(s string, words []string) bool {
	wordLen := len(words[0])
	if len(s) < len(words)*wordLen {
		return false
	}

	if len(words) == 1 && s[:wordLen] == words[0] {
		return true
	}

	words, ok := rmElFromSlice(words, s[:wordLen])

	if ok {
		return helpString(s[wordLen:], words)
	}

	return false
}

func main() {
	fmt.Println(findSubstring("", []string{"dhvf", "sind", "ffsl", "yekr", "zwzq", "kpeo", "cila", "tfty", "modg", "ztjg", "ybty", "heqg", "cpwo", "gdcj", "lnle", "sefg", "vimw", "bxcb"}))
	fmt.Println(findSubstring("barfoofoobarthefoobarman", []string{"bar", "foo", "the"}))
}
